public class ConversionNegativeToPositive
{

	/**
	 * @param args
	 */
	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
		int number = -90;
		 if(number<0)
		 {
			 int positive=number*-1;
			 System.out.println("The number " +number+ "is converted to " +positive+ " number");
		
		 }

    }
}
